import React from 'react'
import TwoChild from '../../Components/TwoChild/TwoChild'
import Marquee from '../../Components/Marquee/Marquee'
import Slider from '../../Components/Slider/Slider'
import ServiceDemoData from './serviceDemoData';
import Stats from '../../Components/Stats/Stats';
import appsData from './casestudyDemoSata';
const Home = () => {
  return (
    <>
      <TwoChild 
      imgs={<img src='/images/hero-.png'></img>}
      text={
        <>
        <h1>Empower Your Business with <span>Qubit Technologies</span></h1>
        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Reprehenderit et labore eaque officiis. Placeat sit sed ex, deleniti corporis eos, inventore harum quae, libero adipisci molestiae quidem error nesciunt accusamus.</p>
        <button className='button2'>Book Demo</button>
        </>
      }
      />

      <Marquee />
      <Slider>
  {ServiceDemoData.map((item, index) => (
    <div key={index} className='item'>
      {item.heading.split(" ").map((word, index) => (
        <h1 key={index} style={{lineHeight:'1.5rem'}}>
          {word}
          <br />
        </h1>
      ))}

      <p>{item.description}</p>

      {item.icon && <span className='icon'>{item.icon}</span>}  {/* Display icon if available */}

      {item.list && (
        <ul>
          {item.list.map((listItem, listIndex) => (
            <li key={listIndex}>
              {listItem}
            </li>
          ))}
        </ul>
      )}

      {item.button && (
        <div className="item_link">
        <button className={index === 0 ? "button2" :"button1"}>
          {item.button.text}
        </button>
        </div>
      )}
    </div>
  ))}
      </Slider>

    <TwoChild 
        imgs={<img src='/images/hero.svg'></img>}
        text={
          <>
          <h1>Why Choose <span>Qubit Technologies</span></h1>
          <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Reprehenderit et labore eaque officiis. Placeat sit sed ex, deleniti corporis eos, inventore harum quae, libero adipisci molestiae quidem error nesciunt accusamus.</p>
          <button className='button2'>Book a Demo</button>
          </>
        }
    />

    <Stats />

    <Slider styles={{
      maxHeight:'40rem',
      overflowY:'hidden',
      overflowX:'auto',    
      backgroundColor:'var(--c1)'
    }} 
    wraperStyle={{
      flexWrap:'nowrap',
    }}
    >   
    {appsData.map((item, index) => (
      <>      
      <button onClick={prevSlide}>Prev</button>

          <div className='item2' key={index}>
          <h1>{item.title}</h1>
          <div className='item2_p'>
            {item.features.map((itemP, indexP) => (
              <>
              <p key={indexP}>{itemP}</p>
              </>
            ))}
          </div>
        </div>
        <button onClick={nextSlide}>Next</button>

      </>
    ))}

    </Slider>
    </>
  )
}

export default Home;
