import React, { forwardRef, useImperativeHandle, useRef } from 'react'
import './Slider.css';

const Slider = forwardRef(({children,styles,wraperStyle}, ref) => {

  const containerRef = useRef();

  const nextSlide = () => {
    containerRef.current.scrollBy({
      left: 10 * parseFloat(getComputedStyle(document.documentElement).fontSize), // 10rem to pixels
      behavior: "smooth",
    });
  };

  const prevSlide = () => {
    containerRef.current.scrollBy({
      left: -10 * parseFloat(getComputedStyle(document.documentElement).fontSize), // -10rem to pixels
      behavior: "smooth",
    });
  };

    // Expose nextSlide and prevSlide to parent
    useImperativeHandle(ref, () => ({
      nextSlide,
      prevSlide,
    }));

  return (
    <>
    <div className="wraper_container" style={styles}>
        <div className="wraper" style={wraperStyle} ref={containerRef}>
        {children}
        </div>
    </div>
    </>
  );
});

export default Slider;
